mapper.select:
    SELECT a.id,a.restaurant_name,a.revenue_name,a.business_date,a.item_sales,a.total_sales,
    a.total_tax FROM report_day_sales a
    
      
        AND a.restaurant_id = #{restaurantId,jdbcType=INTEGER}
      
      
        AND a.revenue_id = #{revenueId,jdbcType=INTEGER}
      
      
         and
	      a.business_date >= STR_TO_DATE(#{startTime,jdbcType=VARCHAR}, '%m/%d/%Y')  AND  a.business_date <= date_add(STR_TO_DATE(#{endTime,jdbcType=VARCHAR}, '%m/%d/%Y'),interval 1 day) 
	     
      
        GROUP BY DATE(a.business_date)
    
     
		  limit  #{start,jdbcType=INTEGER}, #{end,jdbcType=INTEGER}

    SELECT count(*)
     FROM report_day_sales a
    
      
        AND a.restaurant_id = #{restaurantId,jdbcType=INTEGER}
      
      
        AND a.revenue_id = #{revenueId,jdbcType=INTEGER}
      
      
         and
	      a.business_date >= STR_TO_DATE(#{startTime,jdbcType=VARCHAR}, '%m/%d/%Y')  AND  a.business_date <= date_add(STR_TO_DATE(#{endTime,jdbcType=VARCHAR}, '%m/%d/%Y'),interval 1 day)

    SELECT a.id,a.restaurant_name,a.revenue_name,a.business_date,SUM(a.item_sales) item_sales 
    ,SUM(a.total_sales) total_sales,SUM(a.total_tax) total_tax FROM report_day_sales a
    
      
        AND a.restaurant_id = #{restaurantId,jdbcType=INTEGER}
      
      
        AND a.revenue_id = #{revenueId,jdbcType=INTEGER}
      
      
         and
	      a.business_date >= STR_TO_DATE(#{startTime,jdbcType=VARCHAR}, '%m/%d/%Y')  AND  a.business_date <= date_add(STR_TO_DATE(#{endTime,jdbcType=VARCHAR}, '%m/%d/%Y'),interval 1 day) 
	     
      
     GROUP BY a.business_date
      
		  limit  #{start,jdbcType=INTEGER}, #{end,jdbcType=INTEGER}

SELECT COUNT(*) FROM (SELECT COUNT(*) FROM report_day_sales a
AND a.restaurant_id = #{restaurantId,jdbcType=INTEGER}
AND a.revenue_id = #{revenueId,jdbcType=INTEGER}
and a.business_date >= STR_TO_DATE(#{startTime,jdbcType=VARCHAR}, '%m/%d/%Y') AND a.business_date <= date_add(STR_TO_DATE(#{endTime,jdbcType=VARCHAR}, '%m/%d/%Y'),interval 1 day)
GROUP BY DATE(business_date)) t

	SELECT a.id,a.restaurant_id,a.restaurant_name
	,a.revenue_id,a.revenue_name,a.business_date
	,a.item_sales,a.item_sales_qty,a.discount_per,
