	a.last_receipt,(SELECT SUM(b.tax_amount) FROM report_day_tax b WHERE
	a.id=b.day_sales_id) AS total_tax
	FROM report_day_sales a
    
      
        AND a.restaurant_id = #{restaurantId,jdbcType=INTEGER}
      
      
