SELECT SUM(item_sales)item_sales, SUM(item_sales_qty)item_sales_qty,SUM(foc_item)foc_item,SUM(foc_item_qty) foc_item_qty, SUM(total_bill_void)total_bill_void,SUM(total_bill_void_qty)total_bill_void_qty, SUM(discount_per)discount_per, SUM(discount_per_qty)discount_per_qty, SUM(discount)discount, SUM(discount_qty)discount_qty, SUM(discount_amt)discount_amt, SUM(foc_bill) foc_bill, SUM(foc_bill_qty) foc_bill_qty, SUM(total_sales) total_sales, SUM(cash) cash, SUM(cash_qty)cash_qty, SUM(nets)nets, SUM(nets_qty)nets_qty, SUM(visa)visa, SUM(visa_qty)visa_qty, SUM(mc)mc, SUM(mc_qty)mc_qty, SUM(amex)amex, SUM(amex_qty) amex_qty, SUM(jbl)jbl, SUM(jbl_qty)jbl_qty, SUM(union_pay)union_pay, SUM(union_pay_qty)union_pay_qty, SUM(diner)diner, SUM(diner_qty)diner_qty, SUM(holdld)holdld, SUM(holdld_qty)holdld_qty, SUM(total_card)total_card, SUM(total_card_qty)total_card_qty, SUM(total_cash)total_cash, SUM(total_cash_qty)total_cash_qty, SUM(total_void)total_void, SUM(total_void_qty)total_void_qty, SUM(nett_sales)nett_sales, SUM(total_bills)total_bills, SUM(open_count)open_count, SUM(first_receipt)first_receipt,SUM(order_qty)order_qty,SUM(person_qty)person_qty,SUM(total_tax) total_tax, SUM(last_receipt)last_receipt FROM report_day_sales
AND restaurant_id = #{restaurantId,jdbcType=INTEGER}
AND revenue_id = #{revenueId,jdbcType=INTEGER}
and business_date >= STR_TO_DATE(#{startTime,jdbcType=VARCHAR}, '%m/%d/%Y') AND business_date <= date_add(STR_TO_DATE(#{endTime,jdbcType=VARCHAR}, '%m/%d/%Y'),interval 1 day)
and business_date >= STR_TO_DATE(#{businessDate,jdbcType=VARCHAR}, '%m/%d/%Y') AND business_date <= date_add(STR_TO_DATE(#{businessDate,jdbcType=VARCHAR}, '%m/%d/%Y'),interval 1 day)
GROUP BY DATE(business_date)

